import React, { Fragment, useRef, useState } from 'react';
import classes from './AddMovie.module.css';
import ErrorModal from './ErrorModal';




function Edit(props) {

  let titleRef = useRef(props.title);
  let openingTextRef = useRef(props.openingText);
  let releaseDateRef = useRef(props.openingText);

  const [title, setTitle] =  useState(titleRef)

  const [error, setError] = useState();

  function submitHandler(event) {
    event.preventDefault();

    // could add validation here...
    // if(titleRef.current.value.trim().length === 0 || openingTextRef.current.value.trim().length === 0 || releaseDateRef.current.value.trim().length === 0) {
    //   setError({
    //     title: 'Invalid Input',
    //     message: 'Please Try To Fill in all of the fields before adding Movie'
    //   });
    //   return;
    // }

    let Editmovie = {
      title: titleRef.props.title,
      openingText: openingTextRef.props.openingText,
      releaseDate: releaseDateRef.props.releaseDate,

    };

    props.showEditText(Editmovie);

  }

  const errorModalHandler = () => {
    setError(null)
  }


  const ValueChangeHandler = () => {
    value={titleRef}
  }


  return (
    <Fragment>
    {error && <ErrorModal title={error.title} message={error.message} onError={errorModalHandler}></ErrorModal>}
    <form onSubmit={submitHandler}>
      <div className={classes.control}>
        <label htmlFor='title'>Title</label>
        <input type='text' id='title' placeholder={titleRef} />
      </div>
      <div className={classes.control}>
        <label htmlFor='opening-text'>Opening Text</label>
        <textarea rows='5' id='opening-text' value={openingTextRef}></textarea>
      </div>
      <div className={classes.control}>
        <label htmlFor='date'>Release Date</label>
        <input type='date' id='date' value={releaseDateRef} />
      </div>

      <button onClick={() => (props.showEditText)}>Edit Movie</button>
    </form>
    </Fragment>
  );
}

export default Edit;
