import React, { Fragment, useRef, useState } from 'react';
import classes from './AddMovie.module.css';
import ErrorModal from './ErrorModal';




function Edit(props) {


  // let titleRef = useRef(props.title);
  // let openingTextRef = useRef(props.openingText);
  // let releaseDateRef = useRef(props.openingText);

  // const [title, setTitle] =  useState(titleRef)

  // let Editmovie = {

  //   title: props.title,
  //   openingText: props.openingText,
  //   releaseDate: props.releaseDate,

  // // };


  // let Editmovie = {
  //   title: props.showEditText.title,
  //   openingText:props.openingText, 
  //   releaseDate: props.releaseDate,
  //   id: props.id,
  // }




  // console.log(Editmovie)


  const [error, setError] = useState();

  function submitHandler(event) {
    event.preventDefault();

    // could add validation here...
    // if(titleRef.current.value.trim().length === 0 || openingTextRef.current.value.trim().length === 0 || releaseDateRef.current.value.trim().length === 0) {
    //   setError({
    //     title: 'Invalid Input',
    //     message: 'Please Try To Fill in all of the fields before adding Movie'
    //   });
    //   return;
    // }

    props.editMovie(event)


  }



  const errorModalHandler = () => {
    setError(null)
  }

  // const formattedDate = new Date(props.editMovie.releaseDate).toLocaleDateString('en-AU');

  return (
    <Fragment>
    {error && <ErrorModal title={error.title} message={error.message} onError={errorModalHandler}></ErrorModal>}
    <form onSubmit={submitHandler}>
      <div className={classes.control}>
        <label htmlFor='title'>Title</label>
        <input type='text' id='title' value={props.editMovie.title} onChange={props.onChange}/>
      </div>
      <div className={classes.control}>
        <label htmlFor='opening-text'>Opening Text</label>
        <textarea rows='5' id='opening-text' value={props.editMovie.openingText} onChange={props.onChange}></textarea>
      </div>
      <div className={classes.control}>
        <label htmlFor='date'>Release Date</label>
        <input type='date' id='date' value={props.editMovie.releaseDate} onChange={props.onChange}/>
      </div>

      <button onClick={submitHandler}>Edit Movie</button>
    </form>
    </Fragment>
  );
}

export default Edit;
