import React, { Fragment, useRef, useState } from 'react';
import classes from './AddMovie.module.css';





function Edit(props) {
  // const [title, setTitle] =  useState(titleRef)

  function submitHandler(event) {
    event.preventDefault();

    // could add validation here...
    // if(titleRef.current.value.trim().length === 0 || openingTextRef.current.value.trim().length === 0 || releaseDateRef.current.value.trim().length === 0) {
    //   setError({
    //     title: 'Invalid Input',
    //     message: 'Please Try To Fill in all of the fields before adding Movie'
    //   });
    //   return;
    // }

    props.editMovie(event)
  }

  return (
    <Fragment>

    <form onSubmit={submitHandler}>
      <div className={classes.control}>
        <label htmlFor='title'>Title</label>
        <input type='text' id='title' value={props.editMovie.title} onChange={props.onChange}/>
      </div>
      <div className={classes.control}>
        <label htmlFor='opening-text'>Opening Text</label>
        <textarea rows='5' id='opening-text' value={props.editMovie.openingText} onChange={props.onChange}></textarea>
      </div>
      <div className={classes.control}>
        <label htmlFor='date'>Release Date</label>
        <input type='date' id='date' value={props.editMovie.releaseDate} onChange={props.onChange}/>
      </div>

      <button type="submit" onClick={props.onSave}>Edit Movie</button>
    </form>
    </Fragment>
  );
}

export default Edit;
