import React, { useState, useEffect, useCallback } from 'react';

import MoviesList from './components/MoviesList';
import AddMovie from './components/AddMovie';
import Edit from './components/Edit';
import './App.css';
import EditModal from './components/EditModal';

function App() {
  const [movies, setMovies] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);
  let [editMovie, setEditMovie] = useState(null);


  const fetchMoviesHandler = useCallback(async () => {
    setIsLoading(true);
    setError(null);
    try {
      const response = await fetch('https://reactproject-78ba1-default-rtdb.asia-southeast1.firebasedatabase.app/movies.json');
      if (!response.ok) {
        throw new Error('Something went wrong!');
      }

      const data = await response.json();

      const loadedMovies = [];

      for(const key in data) {
        loadedMovies.push({
          id: key,
          title: data[key].title,
          openingText: data[key].openingText,
          releaseDate: data[key].releaseDate,
        });
      }
      setMovies(loadedMovies);
    } catch (error) {
      setError(error.message);
    }
    setIsLoading(false);
  }, []);

  useEffect(() => {
    fetchMoviesHandler();
  }, [fetchMoviesHandler]);


  // const addingMoviewHandler = () => {

  // }


  async function addMovieHandler(movie) {
    const repsonse = await fetch('https://reactproject-78ba1-default-rtdb.asia-southeast1.firebasedatabase.app/movies.json', {
      method: 'POST',
      body: JSON.stringify(movie),
      headers: {
        'Content-type' : 'application/json'
      }
    });
    const data = await repsonse.json();

    fetchMoviesHandler()


  }

  async function removeMovieHandler(movie) {

    // console.log(movie);
    const repsonse = await fetch(`https://reactproject-78ba1-default-rtdb.asia-southeast1.firebasedatabase.app/movies/${movie}.json`, {
      method: 'DELETE',
      body: JSON.stringify(movie),
      headers: {
        'Content-type' : 'application/json'
      }
    });
    const data = await repsonse.json();

    fetchMoviesHandler()
  }


  const EditMovieHandler = movie => {
    console.log('In App.js');
    console.log(movie);

    let Editmovie = {
      title: movie.title,
      openingText:movie.openingText, 
      releaseDate: movie.releaseDate,
      id: movie.id,
    }




    console.log(Editmovie)

  };

  // function EditMovieHandler(movie) {

  //   // const ShowMoviesContent = {
  //   //   title: movie.title,
  //   //   openingText: movie.openingText,
  //   //   releaseDate: movie.releaseDate,

  //   // }

  //   console.log(movie);





  //   // const repsonse = await fetch(`https://reactproject-78ba1-default-rtdb.asia-southeast1.firebasedatabase.app/movies/${movie.id}.json`, {
  //   //   method: 'PATCH',
  //   //   body: JSON.stringify(movie),
  //   //   headers: {
  //   //     'Content-type' : 'application/json'
  //   //   }
  //   // });
  //   // const data = await repsonse.json();

  //   // fetchMoviesHandler()
  // }



  let content = <p>Found no movies.</p>;

  if (movies.length > 0) {
    content = <MoviesList movies={movies} onDeleteUser={removeMovieHandler} onEdit={EditMovieHandler}/>;
  }

  if (error) {
    content = <p>{error}</p>;
  }

  if (isLoading) {
    content = <p>Loading...</p>;
  }






  return (
    <React.Fragment>

      <section>

        <AddMovie onAddMovie={addMovieHandler} />
      </section>

      <section>
        <button onClick={fetchMoviesHandler}>Fetch Movies</button>
      </section>
      <section>
      <Edit showEditText={movies} ></Edit>
      </section>
      <section>{content}</section>
    </React.Fragment>
  );
}

export default App;
