import React, { Fragment, useRef, useState } from "react";
import classes from "./AddMovie.module.css";

const InputView = (props) => {
  return (
    <form onSubmit={props.submitHandler}>
      <div className={classes.control}>
        <label htmlFor="title">{props.title}</label>
        <input type="text" id="title" title={props.title} />
      </div>
      <div className={classes.control}>
        <label htmlFor="opening-text">Opening Text</label>
        <textarea
          rows="5"
          id="opening-text"
          openingText={props.openingText}
        ></textarea>
      </div>
      <div className={classes.control}>
        <label htmlFor="date">Release Date</label>
        <input type="date" id="date" releaseDate={props.releaseDate} />
      </div>
      <button onClick={props.onClick}>Add Movie</button>
    </form>
  );
};

export default InputView;
